mapper.update:
UPDATE POST SET
                TITLE = #{title},
                CONTENT = #{content},
                CATEGORY_ID = #{categoryId},
                UPDATED_AT = #{updatedAt}
WHERE ID = #{id}

UPDATE POST SET
    VIEWS = VIEWS + 1
WHERE ID = #{id}

mapper.select:
SELECT *
FROM (
         SELECT ROWNUM RN, *
         FROM POST
         WHERE CATEGORY_ID = #{categoryId}
         ORDER BY #{sort} DESC
     )
WHERE RN BETWEEN (#{pageNum} - 1) * #{pageSize} + 1 AND #{pageNum} * #{pageSize}

SELECT COUNT(ID)
FROM POST
WHERE CATEGORY_ID = #{categoryId}
